mapper.select:
select admin_nickname
from admin
where admin_nickname = #{nickname}

mapper.update:
update user
set nickname = #{nickname}
where
user_number = #{user_number}

mapper.select:
select id
from user
where name = #{name} and email = #{email}

mapper.update:
update user
set pw = #{pw}
where user_number = #{user_number}
