mapper.select:
select nickname
from user
where nickname = #{nickname}

select admin_nickname
from admin
where admin_nickname = #{nickname}

mapper.update:
update user
set nickname = #{nickname}
where user_number = #{user_number}

mapper.select:
select *
from user
where user_number = #{user_number} and pw = #{pw}

select *
from user
where user_number = #{user_number} and email = #{email}

mapper.delete:
delete
from dip
where user_number = #{user_number} and buytogether_number = #{buytogether_number}
